package com.bikesystem.entity;
/**
 * 店铺信息表：shop对应实体类
 * @author bwfadmin
 *
 */
public class Shop {
	private int sid;
	private int aid;
	private String shopName;
	private String shopAddress;
	private String shopBuildTime;
	private String shopInformation;
	
	
	public Shop() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Shop(int sid, int aid, String shopName, String shopAddress, String shopBuildTime, String shopInformation) {
		super();
		this.sid = sid;
		this.aid = aid;
		this.shopName = shopName;
		this.shopAddress = shopAddress;
		this.shopBuildTime = shopBuildTime;
		this.shopInformation = shopInformation;
	}
	
	public Shop(int aid, String shopName, String shopAddress, String shopBuildTime, String shopInformation) {
		super();
		this.aid = aid;
		this.shopName = shopName;
		this.shopAddress = shopAddress;
		this.shopBuildTime = shopBuildTime;
		this.shopInformation = shopInformation;
	}
	
	public int getSid() {
		return sid;
	}
	public void setSid(int sid) {
		this.sid = sid;
	}
	public int getAid() {
		return aid;
	}
	public void setAid(int aid) {
		this.aid = aid;
	}
	public String getShopName() {
		return shopName;
	}
	public void setShopName(String shopName) {
		this.shopName = shopName;
	}
	public String getShopAddress() {
		return shopAddress;
	}
	public void setShopAddress(String shopAddress) {
		this.shopAddress = shopAddress;
	}
	public String getShopBuildTime() {
		return shopBuildTime;
	}
	public void setShopBuildTime(String shopBuildTime) {
		this.shopBuildTime = shopBuildTime;
	}
	public String getShopInformation() {
		return shopInformation;
	}
	public void setShopInformation(String shopInformation) {
		this.shopInformation = shopInformation;
	}
	@Override
	public String toString() {
		return "Shop [sid=" + sid + ", aid=" + aid + ", shopName=" + shopName + ", shopAddress=" + shopAddress
				+ ", shopBuildTime=" + shopBuildTime + ", shopInformation=" + shopInformation + "]";
	}
	
}	
